import requests

url = "https://everyearthquake.p.rapidapi.com/recentEarthquakes"
querystring = {"count": count, "radius": radius, "units": units, "intensity": intensity, "magnitude": magnitude, "longitude": longitude, "start": start, "latitude": latitude, "type": type, "interval": interval}

headers = {
    "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
    "X-RapidAPI-Host": "everyearthquake.p.rapidapi.com"
}

response = requests.get(url, headers=headers, params=querystring)

print(response.json())

